				end
				if g_time > API.util.t_ms() or s_time > API.util.t_ms() then
					if g_time > API.util.t_ms() then size = size *1.01
					else size = size *.99
					end
				else size=1
				end
				if size >= 1.25 then
					s_time = API.util.t_ms()+125
					g_time = API.util.t_ms()
				end
			else
				size=1
			end
