	for i=1,#_table do
		text=""
		for t=0,7 do
			if _table[max_num(#_table,i+t)] ~= nil then
				text=text.._table[max_num(#_table,i+t)]
			end
		end
		_table2[#_table2+1]=text
	end
	return _table2
end

