	if g.tbl.PID_INFO.plyr_god[_pid+1] and not g.tbl.PID_INFO.loading[_pid+1] and not g.tbl.PID_INFO.interior[_pid+1] and (API.ent.get.speed(API.plyr.get.ped(_pid)) > 3 or g.tbl.PID_INFO.plyr_moving[_pid+1]) then
